import React, { useEffect } from 'react'
import { Button, Table, Row, Col, Pagination } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import {
  listProjects,
  deleteProject,
  createProject,
} from '../actions/projectAction'
import Loading from '../components/Loading'
import ErrorMessageBox from '../components/ErrorMessagebox'
import { PROJECT_CREATE_RESET } from '../constants/projectConstant'
import { useNavigate, useParams } from 'react-router-dom'

export default function ProjectListScreen() {
  const navigate = useNavigate()
  const dispatch = useDispatch()
  const createHandler = () => {
    dispatch(createProject())
  }
  const deleteHandler = (project) => {
    if (window.confirm('Are you sure?')) {
      dispatch(deleteProject(project._id))
    }
  }

  // const projectDelete = useSelector((state) => state.projectDelete)
  // const {
  //   success: successDelete,
  //   loading: loadingDelete,
  //   error: errorDelete,
  // } = projectDelete
  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin
  const projectList = useSelector((state) => state.projectList)
  const { loading, error, projects, pages, page } = projectList

  // const projectCreate = useSelector((state) => state.projectCreate)
  // const {
  //   success: successCreate,
  //   loading: loadingCreate,
  //   error: errorCreate,
  //   project: createdProduct,
  // } = projectCreate

  useEffect(
    () => {
      // if (successCreate) {
      //   dispatch({ type: PROJECT_CREATE_RESET })
      //   navigate(`/product/${createdProduct._id}/edit`)
      // } else {
      dispatch(listProjects())
      // }
    }
    // , [successCreate, pageNumber, dispatch, , userInfo, successDelete]
  )
  return (
    <>
      <Row className="align-items-center">
        <Col>
          <h1>Projects</h1>
        </Col>
        <Col className="text-right">
          <Button onClick={createHandler}>Create Product</Button>
        </Col>
      </Row>
      {loading ? (
        <Loading></Loading>
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <>
          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>ID</th>
                <th>NAME</th>
                <th>BY</th>
                <th>CATEGORY</th>
                <th>IMAGE</th>
                <th>donationGoal</th>
                <th>Location</th>
                <th>description</th>
                <th>Challenge</th>
                <th>Solution</th>
                <th>LongTermImpact</th>
                <th>additionalDocumentation</th>
                <th>bankAccountNo</th>
                <th>companyRegistrationNumber</th>
                <th>companyAddress</th>
                <th>yearFounded</th>
                <th>contactName</th>
                <th>emailAddress</th>
                <th>country</th>
                <th>telephoneNo</th>
                <th>ACTIONS</th>
              </tr>
            </thead>
            <tbody>
              {projects.map((project) => (
                <tr key={project._id}>
                  <td>{project._id}</td>
                  <td>{project.name}</td>
                  <td>{project.by}</td>
                  <td>{project.category}</td>
                  <td>{project.image}</td>
                  <td>{project.donationGoal}</td>
                  <td></td>Location
                  <td>{project.description}</td>
                  <td>{project.Challenge}</td>
                  <td>{project.Solution}</td>
                  <td>{project.LongTermImpact}</td>
                  <td>{project.additionalDocumentation}</td>
                  <td>{project.bankAccountNo}</td>
                  <td>{project.companyRegistrationNumber}</td>
                  <td>{project.companyAddress}</td>
                  <td>{project.yearFounded}</td>
                  <td>{project.contactName}</td>
                  <td>{project.emailAddress}</td>
                  <td>{project.country}</td>
                  <td>{project.telephoneNo}</td>
                  <td>{project.country}</td>
                  <td>
                    <LinkContainer to={`/projects/${project._id}/edit`}>
                      <Button variant="light" className="btn-sm">
                        Edit
                      </Button>
                    </LinkContainer>
                    <Button
                      type="button"
                      variant="light"
                      className="btn-sm"
                      onClick={() => deleteHandler(project)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
          <Pagination>
            {[...Array(pages).keys()].map((x) => (
              <LinkContainer key={x + 1} to={`/projectlist/${x + 1}`}>
                <Pagination.Item active={x + 1 === page}>
                  {x + 1}
                </Pagination.Item>
              </LinkContainer>
            ))}
          </Pagination>
        </>
      )}
    </>
  )
}
