import React, { useState } from 'react'
import { Form, Button, Container } from 'react-bootstrap'
import { useEffect } from 'react'
import { detailsOfProject, updateProject } from '../actions/projectAction'
import { useDispatch, useSelector } from 'react-redux'
import Loading from '../components/Loading'
import ErrorMessageBox from '../components/ErrorMessagebox'
import { PROJECT_UPDATE_RESET } from '../constants/projectConstant'
import { useNavigate, useParams } from 'react-router-dom'

export default function ProjectEditScreen(props) {
  const navigate = useNavigate()
  const params = useParams()
  const { id: projectId } = params
  const [uploading, setUploading] = useState(false)
  const [name, setName] = useState('')
  const [category, setCategory] = useState()
  const [image, setImage] = useState('')
  const [description, setDescription] = useState('')
  const [Challenge, setChallenge] = useState('')
  const [Solution, setSolution] = useState('')
  const [LongTermImpact, setLongTermImpact] = useState('')
  const [additionalDocumentation, setAdditionalDocumentation] = useState('')

  const projectUpdate = useSelector((state) => state.projectUpdate)
  const {
    error: errorUpdate,
    loading: loadingUpdate,
    success: successUpdate,
  } = projectUpdate

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(
      updateProject({
        _id: projectId,
        name,
        category,
        image,
        description,
        Challenge,
        Solution,
        LongTermImpact,
        additionalDocumentation,
      })
    )
  }

  const projectDetails = useSelector((state) => state.projectDetails)
  const { project, loading, error } = projectDetails
  const dispatch = useDispatch()
  useEffect(() => {
    if (successUpdate) {
      dispatch({ type: PROJECT_UPDATE_RESET })
      navigate('/adminDashboard')
    }
    if (!project.name || project._id !== projectId) {
      dispatch(detailsOfProject(projectId))
    } else {
      setName(project.name)
      setCategory(project.category)
      setImage(project.image)
      setDescription(project.description)
      setChallenge(project.Challenge)
      setSolution(project.Solution)
      setLongTermImpact(project.LongTermImpact)
      setAdditionalDocumentation(project.additionalDocumentation)
    }
  }, [project, successUpdate, navigate, dispatch])

  // const uploadFileHandler = (e) => {
  //   const file = e.target.files[0]
  //   const formData = new FormData()
  //   formData.append('image', file)
  //   setUploading(true)
  //   Axios.post('/api/uploads', formData, {
  //     headers: {
  //       'Content-Type': 'multipart/form-data',
  //     },
  //   })
  //     .then((response) => {
  //       setImage(response.data)
  //       setUploading(false)
  //     })
  //     .catch((error) => {
  //       console.log(error)
  //       setUploading(false)
  //     })
  // }

  return (
    <Container>
      <h1>Edit Project {project.name}</h1>
      {loadingUpdate && <Loading></Loading>}
      {errorUpdate && (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      )}
      {loading ? (
        <Loading></Loading>
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <Form onSubmit={submitHandler}>
          <Form.Group controlId="name">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group controlId="category">
            <Form.Label>category</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter category"
              value={category}
              onChange={(e) => setCategory(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group controlId="image">
            <Form.Label>Image</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter image"
              value={image}
              onChange={(e) => setImage(e.target.value)}
            ></Form.Control>
            {/* <Form.File
              id="image-file"
              label="Choose Image"
              custom
              onChange={uploadFileHandler}
            ></Form.File>
            {uploading && <Loading />} */}
          </Form.Group>
          <Form.Group controlId="description">
            <Form.Label>description</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group controlId="Challenge">
            <Form.Label>Challenge</Form.Label>
            <Form.Control
              type="textarea"
              placeholder="Enter Challenge"
              value={Challenge}
              onChange={(e) => setChallenge(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group controlId="Solution">
            <Form.Label>Solution</Form.Label>
            <Form.Control
              type="textarea"
              placeholder="Enter Solution"
              value={Solution}
              onChange={(e) => setSolution(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group controlId="LongTermImpact">
            <Form.Label>LongTermImpact</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Enter LongTermImpact"
              value={LongTermImpact}
              onChange={(e) => setLongTermImpact(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group controlId="additionalDocumentation">
            <Form.Label>additionalDocumentation</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Enter additionalDocumentation"
              value={additionalDocumentation}
              onChange={(e) => setAdditionalDocumentation(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Button variant="primary" type="submit">
            Update
          </Button>{' '}
          <Button
            variant="light"
            type="button"
            onClick={() => navigate('/adminDashboard')}
          >
            Back
          </Button>
        </Form>
      )}
    </Container>
  )
}
