import React, { useEffect, useState } from 'react'
import { Button, Container, Form } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { detailsUser, updateUserProfile } from '../actions/userAction'
import ErrorMessageBox from '../components/ErrorMessagebox'
import Loading from '../components/Loading'

export const ProfileScreen = () => {
  const navigate = useNavigate()
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [Confirmpassword, setConfirmPassword] = useState('')
  const [message, setMessage] = useState(null)

  const updateProfile = useSelector((state) => state.updateProfile)
  const { success } = updateProfile

  const userDetail = useSelector((state) => state.userDetail)
  const { loading, error, user } = userDetail

  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin
  if (!userInfo) {
    navigate('/signin')
  }

  const dispatch = useDispatch()

  useEffect(() => {
    if (!user.name) {
      dispatch(detailsUser(userInfo._id))
      console.log(detailsUser())
    } else {
      setName(user.name)
      setEmail(user.email)
    }
  }, [dispatch, user, userInfo._id])

  const submitHandler = (e) => {
    e.preventDefault()
    if (password !== Confirmpassword) {
      setMessage('Passwords do not match')
    } else {
      dispatch(
        updateUserProfile({
          userId: user._id,
          name,
          email,
          password,
        })
      )
    }
  }

  return (
    <Container className="small-container">
      <h1 className="my-3">Profile</h1>
      {message && <ErrorMessageBox variant="danger">{message}</ErrorMessageBox>}
      {error && <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>}
      {success && (
        <ErrorMessageBox variant="success">Profile Updated</ErrorMessageBox>
      )}
      {loading && <Loading />}
      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="name">
          <Form.Label>Full Name</Form.Label>
          <Form.Control
            type="name"
            placeholder="name"
            value={name}
            required
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="email"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="confirmPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </Form.Group>
        <div className="mb-3">
          <Button type="submit">Update Details</Button>
        </div>
      </Form>
    </Container>
  )
}
