import {
  PAYMENT_PRODUCT_DETAIL,
  SAVE_PAYMENT_INFORMATION,
} from '../constants/paymentConstant'

export const PaymentReducer = (
  state = { projectItem: [], shippingAddress: {} },
  action
) => {
  switch (action.type) {
    case PAYMENT_PRODUCT_DETAIL:
      return { ...state, projectItem: action.payload }
    case SAVE_PAYMENT_INFORMATION:
      return { ...state, savePayment: action.payload }
    //   case CART_SAVE_PAYMENT_METHOD:
    //     return { ...state, paymentMethod: action.payload };
    //   case CART_EMPTY:
    // return { cartItems: [], shippingAddress: {} };
    default:
      return state
  }
}
