import {
  PROJECT_LIST_REQUEST,
  PROJECT_LIST_SUCCESS,
  PROJECT_LIST_FAIL,
  PROJECT_DETAILS_REQUEST,
  PROJECT_DETAILS_SUCCESS,
  PROJECT_DETAILS_FAIL,
  PROJECT_SAVE_REQUEST,
  PROJECT_SAVE_SUCCESS,
  PROJECT_SAVE_FAIL,
  PROJECT_DELETE_REQUEST,
  PROJECT_DELETE_SUCCESS,
  PROJECT_DELETE_FAIL,
  PROJECT_REVIEW_SAVE_SUCCESS,
  PROJECT_REVIEW_SAVE_REQUEST,
  PROJECT_REVIEW_SAVE_FAIL,
  PROJECT_REVIEW_SAVE_RESET,
} from '../constants/projectConstant'

function projectListReducer(state = { donation: [] }, action) {
  switch (action.type) {
    case PROJECT_LIST_REQUEST:
      return { loading: true, donation: [] }
    case PROJECT_LIST_SUCCESS:
      return { loading: false, donation: action.payload }
    case PROJECT_LIST_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

function projectDetailsReducer(state = { product: { reviews: [] } }, action) {
  switch (action.type) {
    case PROJECT_DETAILS_REQUEST:
      return { loading: true }
    case PROJECT_DETAILS_SUCCESS:
      return { loading: false, product: action.payload }
    case PROJECT_DETAILS_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

function projectDeleteReducer(state = { product: {} }, action) {
  switch (action.type) {
    case PROJECT_DELETE_REQUEST:
      return { loading: true }
    case PROJECT_DELETE_SUCCESS:
      return { loading: false, product: action.payload, success: true }
    case PROJECT_DELETE_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

function projectSaveReducer(state = { product: {} }, action) {
  switch (action.type) {
    case PROJECT_SAVE_REQUEST:
      return { loading: true }
    case PROJECT_SAVE_SUCCESS:
      return { loading: false, success: true, product: action.payload }
    case PROJECT_SAVE_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}
function projectReviewSaveReducer(state = {}, action) {
  switch (action.type) {
    case PROJECT_REVIEW_SAVE_REQUEST:
      return { loading: true }
    case PROJECT_REVIEW_SAVE_SUCCESS:
      return { loading: false, review: action.payload, success: true }
    case PROJECT_REVIEW_SAVE_FAIL:
      return { loading: false, errror: action.payload }
    case PROJECT_REVIEW_SAVE_RESET:
      return {}
    default:
      return state
  }
}

export {
  projectListReducer,
  projectDetailsReducer,
  projectSaveReducer,
  projectDeleteReducer,
  projectReviewSaveReducer,
}
