import React, { useEffect } from 'react'
import { Row, Col, Pagination } from 'react-bootstrap'
import Project from '../components/Projects'
import { useDispatch, useSelector } from 'react-redux'
import { listProjects } from '../actions/projectAction'
import Loading from '../components/Loading'
import ErrorMessageBox from '../components/ErrorMessagebox'
import { useParams } from 'react-router-dom'
import { LinkContainer } from 'react-router-bootstrap'

export default function StartProjectScreen() {
  const params = useParams()
  const keyword = params.keyword
  const pageNumber = params.pageNumber || 1
  const dispatch = useDispatch()
  const projectList = useSelector((state) => state.projectList)
  const { project, pages, page, loading, error } = projectList

  useEffect(() => {
    dispatch(listProjects({ keyword, pageNumber }))
    dispatch(project)
  }, [pageNumber, keyword, dispatch, project])
  return (
    <>
      {loading ? (
        <Loading />
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <>
          <h1>Featured Products</h1>
          <Row>
            {project.map((project) => (
              <Col key={project._id} sm={12} md={6} lg={3}>
                <Project product={project}></Project>
              </Col>
            ))}
          </Row>
          <Pagination>
            {[...Array(pages).keys()].map((x) => (
              <LinkContainer
                key={x + 1}
                to={
                  keyword
                    ? `/search/${keyword}/page/${x + 1}`
                    : `/page/${x + 1}`
                }
              >
                <Pagination.Item active={x + 1 === page}>
                  {x + 1}
                </Pagination.Item>
              </LinkContainer>
            ))}
          </Pagination>
        </>
      )}
    </>
  )
}
