import React, { useState } from 'react'
import { Form, Button, Container } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Loading from '../components/Loading'
import { createProject } from '../actions/projectAction'
import ErrorMessageBox from '../components/ErrorMessagebox'
import {
  PROJECT_UPLOAD_REQUEST,
  PROJECT_UPLOAD_SUCCESS,
  PROJECT_UPLOAD_FAIL,
} from '../constants/projectConstant'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'

export default function ProjectCreateScreen() {
  const navigate = useNavigate()
  const [uploading, setUploading] = useState(false)
  const [name, setName] = useState('')
  const [category, setCategory] = useState()
  const [image, setImage] = useState('')
  const [description, setDescription] = useState('')
  const [Challenge, setChallenge] = useState('')
  const [Solution, setSolution] = useState('')
  const [longTermImpact, setLongTermImpact] = useState('')
  const [additionalDocumentation, setAdditionalDocumentation] = useState('')
  const [by, setBy] = useState('')
  const [location, setLocation] = useState('')
  const [donationGoal, setDonationGoal] = useState('')
  const [companyRegistrationNumber, setCompanyRegistrationNumber] = useState('')
  const [companyAddress, setCompanyAddress] = useState('')
  const [yearFounded, setYearFounded] = useState('')
  const [contactName, setContactName] = useState('')
  const [telephoneNo, setTelephoneNo] = useState('')
  const [emailAddress, setEmailAddress] = useState('')
  const [country, setCountry] = useState('')
  const dispatch = useDispatch()

  const projectCreate = useSelector((state) => state.projectCreate)
  const { success, loading, error } = projectCreate

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(
      createProject({
        name,
        category,
        image,
        description,
        Challenge,
        Solution,
        longTermImpact,
        additionalDocumentation,
        by,
        location,
        donationGoal,
        companyRegistrationNumber,
        companyAddress,
        yearFounded,
        contactName,
        telephoneNo,
        emailAddress,
        country,
      })
    )
  }

  const uploadFileHandler = async (e) => {
    const file = e.target.files[0]
    const bodyformData = new FormData()
    bodyformData.append('file', file)
    setUploading(true)
    try {
      dispatch({ type: PROJECT_UPLOAD_REQUEST })
      const { data } = await axios.post('/api/upload', bodyformData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      })
      dispatch({ type: PROJECT_UPLOAD_SUCCESS })
      setImage(data.secure_url)
      setUploading(false)
    } catch (error) {
      dispatch({ type: PROJECT_UPLOAD_FAIL })
      console.error(error)
      setUploading(false)
    }
  }

  return (
    <Container>
      <h1>Create Project</h1>
      {success && <ErrorMessageBox variant="danger">{success}</ErrorMessageBox>}
      {error && <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>}
      {loading && <Loading />}

      <Form onSubmit={submitHandler}>
        <Form.Group controlId="name">
          <Form.Label>Project Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Project Name"
            value={name}
            required
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="Name of organization">
          <Form.Label>Name of organization</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter the name of your organization"
            value={by}
            required
            onChange={(e) => setBy(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="charity category">
          <Form.Label>charity category</Form.Label>
          <Form.Control
            type="text"
            placeholder="etc..education/knowledge/food donation.."
            value={category}
            required
            onChange={(e) => setCategory(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Form.Group>
          <Form.Group controlId="image">
            <Form.Label>Image</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter image"
              value={image}
              required
              onChange={(e) => setImage(e.target.value)}
            ></Form.Control>
            <Form.Group controlId="imageFile">
              <Form.Label>Upload File</Form.Label>
              <Form.Control
                type="file"
                onChange={uploadFileHandler}
              ></Form.Control>
              {uploading && <Loading />}
            </Form.Group>
          </Form.Group>
        </Form.Group>
        <Form.Group controlId="Donation Goal">
          <Form.Label>Donation Goal</Form.Label>
          <Form.Control
            type="number"
            min="0"
            placeholder="$ 1000 etc..."
            value={donationGoal}
            required
            onChange={(e) => setDonationGoal(e.target.value)}
          />
        </Form.Group>
