const {
  PROJECT_LIST_REQUEST,
  PROJECT_LIST_SUCCESS,
  PROJECT_LIST_FAIL,
  PROJECT_DETAILS_REQUEST,
  PROJECT_DETAILS_SUCCESS,
  PROJECT_DETAILS_FAIL,
  PROJECT_DELETE_REQUEST,
  PROJECT_DELETE_SUCCESS,
  PROJECT_DELETE_FAIL,
  PROJECT_CREATE_REQUEST,
  PROJECT_CREATE_SUCCESS,
  PROJECT_CREATE_FAIL,
  PROJECT_CREATE_RESET,
  PROJECT_UPDATE_REQUEST,
  PROJECT_UPDATE_SUCCESS,
  PROJECT_UPDATE_FAIL,
  PROJECT_UPDATE_RESET,
  PROJECT_REVIEW_SAVE_REQUEST,
  PROJECT_REVIEW_SAVE_SUCCESS,
  PROJECT_REVIEW_SAVE_FAIL,
  PROJECT_REVIEW_SAVE_RESET,
} = require('../constants/projectConstant')

export function productListReducer(state = { donation: [] }, action) {
  switch (action.type) {
    case PROJECT_LIST_REQUEST:
      return { loading: true, donation: [] }
    case PROJECT_LIST_SUCCESS:
      return {
        loading: false,
        donation: action.payload.donation,
        pages: action.payload.pages,
        page: action.payload.page,
      }
    case PROJECT_LIST_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

export const productCreateReducer = (state = {}, action) => {
  switch (action.type) {
    case PROJECT_CREATE_REQUEST:
      return { loading: true }
    case PROJECT_CREATE_SUCCESS:
      return { loading: false, project: action.payload, success: true }
    case PROJECT_CREATE_FAIL:
      return { loading: false, error: action.payload }
    case PROJECT_CREATE_RESET:
      return {}
    default:
      return state
  }
}

export const productReviewSaveReducer = (state = {}, action) => {
  switch (action.type) {
    case PROJECT_REVIEW_SAVE_REQUEST:
      return { loading: true }
    case PROJECT_REVIEW_SAVE_SUCCESS:
      return { loading: false, project: action.payload, success: true }
    case PROJECT_REVIEW_SAVE_FAIL:
      return { loading: false, error: action.payload }
    case PROJECT_REVIEW_SAVE_RESET:
      return {}
    default:
      return state
  }
}

export const productDeleteReducer = (state = {}, action) => {
  switch (action.type) {
    case PROJECT_DELETE_REQUEST:
      return { loadig: true }
    case PROJECT_DELETE_SUCCESS:
      return { loadig: false, success: true }
    case PROJECT_DELETE_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

export function productDetailsReducer(
  state = { loading: true, product: { reviews: [] } },
  action
) {
  switch (action.type) {
    case PROJECT_DETAILS_REQUEST:
      return { ...state, loading: true }
    case PROJECT_DETAILS_SUCCESS:
      return { loading: false, product: action.payload }
    case PROJECT_DETAILS_FAIL:
      return { loading: false, error: action.payload }
    default:
      return state
  }
}

export const productUpdateReducer = (state = { product: {} }, action) => {
  switch (action.type) {
    case PROJECT_UPDATE_REQUEST:
      return { loading: true }
    case PROJECT_UPDATE_SUCCESS:
      return { loading: false, success: true, product: action.payload }
    case PROJECT_UPDATE_FAIL:
      return { loading: false, error: action.payload }
    case PROJECT_UPDATE_RESET:
      return { product: {} }
    default:
      return state
  }
}
