import React, { useEffect } from 'react'
import { Link, useNavigate, useParams } from 'react-router-dom'
import {
  Image,
  ListGroup,
  Row,
  Col,
  Card,
  Button,
  Form,
  ListGroupItem,
  Container,
} from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Rating from '../components/Rating'
import { detailsOfProject } from '../actions/projectAction'
import ErrorMessageBox from '../components/ErrorMessagebox'
import Loading from '../components/Loading'

export default function ProductScreen() {
  //const navigate = useNavigate()
  const params = useParams()
  const dispatch = useDispatch()
  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin
  const projectDetails = useSelector((state) => state.projectDetails)
  const { product, loading, error } = projectDetails

  useEffect(() => {
    dispatch(detailsOfProject(params.id))
  })

  return (
    <>
      <Link to="/">Back to result</Link>
      {loading ? (
        <Loading />
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <Container className="mt-5">
          <Row>
            <Col md={3}>
              <Image src={product.image} alt={product.name} fluid />
            </Col>
            <Col md={6}>
              <ListGroup variant="flush">
                <ListGroupItem>
                  <h3>{product.name}</h3>
                </ListGroupItem>
                <ListGroupItem>
                  <h6>
                    <strong>Location: </strong> {product.Location}
                  </h6>
                  <h6>
                    <strong>By: </strong> {product.by}
                  </h6>
                </ListGroupItem>
                <ListGroupItem>
                  <p>
                    <strong>Description: </strong> {product.description}
                  </p>
                  <p>
                    <strong>Challenge:</strong> {product.Challenge}
                  </p>
                  <p>
                    <strong>Solution: </strong>
                    {product.Solution}
                  </p>
                  <p>
                    <strong>Longterm Impact:</strong> {product.LongTermImpact}
                  </p>
                </ListGroupItem>
              </ListGroup>
            </Col>
            <Col md={3}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroupItem>
                    <h5>Category: {product.category}</h5>
                  </ListGroupItem>
                  <ListGroupItem>
                    <div>
                      Status:{' '}
                      {product.donationGoal <= product.currentdonation
                        ? 'Donation goal has been completed'
                        : 'In progress'}
                    </div>
                  </ListGroupItem>
                  <ListGroupItem>
                    <div>Donation needed: ${product.donationGoal}</div>
                    <div>Collected amount: ${product.currentdonation}</div>
                  </ListGroupItem>
                  <ListGroupItem>
                    <Button
                      className="btn-block"
                      type="button"
                      disabled={product.donationGoal <= product.currentdonation}
                    >
                      Donate
                    </Button>
                  </ListGroupItem>
                </ListGroup>
              </Card>
            </Col>
          </Row>
        </Container>
      )}
    </>
  )
}
