import React, { useEffect, useState } from 'react'
import { Button, Container, Form } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate, useParams } from 'react-router-dom'
import { detailsUser, updateUserProfile } from '../actions/userAction'
import ErrorMessageBox from '../components/ErrorMessagebox'
import Loading from '../components/Loading'
import { USER_UPDATE_PROFILE_RESET } from '../constants/userConstant'

export const ProfileScreen = () => {
  const navigate = useNavigate()
  const params = useParams()
  const { _id: userid } = params
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  const [Confirmpassword, setConfirmPassword] = useState('')
  const [message, setMessage] = useState(null)

  const updateProfile = useSelector((state) => state.updateProfile)
  const {
    success: successupdate,
    error: errorUpdate,
    loading: loadingUpdate,
  } = updateProfile

  const userDetail = useSelector((state) => state.userDetail)
  const { loading, error, user } = userDetail

  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin

  const dispatch = useDispatch()

  useEffect(() => {
    if (!userInfo) {
      navigate('/')
      console.log(navigate)
    } else {
      if (!user.name) {
        dispatch({ type: USER_UPDATE_PROFILE_RESET })
        dispatch(detailsUser(userInfo._id))
      } else {
        setName(user.name)
        setEmail(user.email)
      }
    }
  }, [userInfo, dispatch, user, navigate])

  const submitHandler = (e) => {
    e.preventDefault()
    if (password !== Confirmpassword) {
      setMessage('Passwords do not match')
    } else {
      dispatch(
        updateUserProfile({
          _id: userid,
          name,
          email,
          password,
        })
      )
    }
  }

  return (
    <Container className="small-container">
      <h1 className="my-3">Profile</h1>
      {message && <ErrorMessageBox variant="danger">{message}</ErrorMessageBox>}
      {error && <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>}
      {successupdate && (
        <ErrorMessageBox variant="success">Profile Updated</ErrorMessageBox>
      )}
      {errorUpdate && (
        <ErrorMessageBox variant="danger">{errorUpdate}</ErrorMessageBox>
      )}
      {loadingUpdate && <Loading></Loading>}
      {loading && <Loading />}
      <Form onSubmit={submitHandler}>
        <Form.Group className="mb-3" controlId="name">
          <Form.Label>Full Name</Form.Label>
          <Form.Control
            type="name"
            placeholder="Enter your name"
            value={name}
            required
            onChange={(e) => setName(e.target.value)}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter your email"
            value={email}
            required
            onChange={(e) => setEmail(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="password">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setPassword(e.target.value)}
          />
        </Form.Group>
        <Form.Group className="mb-3" controlId="confirmPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            required
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </Form.Group>
        <div className="mb-3">
          <Button type="submit">Update Details</Button>
        </div>
      </Form>
    </Container>
  )
}
