import axios from 'axios'
import { useEffect, useReducer } from 'react'
import {
  Col,
  ListGroup,
  ListGroupItem,
  Row,
  Image,
  Card,
  Button,
} from 'react-bootstrap'
import { useParams } from 'react-router-dom'
//import { Link } from 'react-router-dom'
//import { Row, Col, Image, ListGroup, Card, Button } from 'react-bootstrap'
//import data from '../data'

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true }
    case 'FETCH_SUCCESS':
      return { ...state, donation: action.payload, loading: false }
    case 'FETCH_fail':
      return { ...state, loading: false, error: action.payload }
    default:
      return state
  }
}

function ProjectScreen() {
  const params = useParams()
  const { slug } = params
  const [{ loading, error, donation }, dispatch] = useReducer(reducer, {
    donation: [],
    loading: true,
    error: '',
  })
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' })
      try {
        const result = await axios.get(`/api/donation/slug/${slug}`)
        dispatch({ type: 'FETCH_SUCCESS', payload: result.data })
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message })
      }
    }
    fetchData()
  }, [slug])

  return loading ? (
    <div>Loading...</div>
  ) : error ? (
    <div>{error}</div>
  ) : (
    <>
      <Row>
        <Col md={3}>
          <Image src={donation.image} alt={donation.name} fluid />
        </Col>
        <Col md={6}>
          <ListGroup variant="flush">
            <ListGroupItem>
              <h3>{donation.name}</h3>
            </ListGroupItem>
            <ListGroupItem>
              <h5>
                <strong>Location: </strong> {donation.Location}
              </h5>
            </ListGroupItem>
            <ListGroupItem>
              <p>
                <strong>Description: </strong> {donation.description}
              </p>
              <p>
                <strong>Challenge:</strong> {donation.Challenge}
              </p>
              <p>
                <strong>Solution: </strong>
                {donation.Solution}
              </p>
              <p>
                <strong>Longterm Impact:</strong> {donation.LongTermImpact}
              </p>
            </ListGroupItem>
          </ListGroup>
        </Col>
        <Col md={3}>
          <Card>
            <ListGroup variant="flush">
              <ListGroupItem>
                <h5>Category: {donation.category}</h5>
              </ListGroupItem>
              <ListGroupItem>
                <div>Donation needed: ${donation.donationGoal}</div>
                <div>Collected amount: ${donation.currentdonation}</div>
              </ListGroupItem>
              <ListGroupItem>
                <Row>
                  <Button className="btn-block" type="button">
                    Donate
                  </Button>
                </Row>
              </ListGroupItem>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </>
  )
}
export default ProjectScreen
