import React, { useEffect } from 'react'
import { Link, useParams } from 'react-router-dom'
import {
  Image,
  ListGroup,
  Row,
  Col,
  Card,
  Button,
  ListGroupItem,
  Container,
} from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import { detailsOfProject } from '../actions/projectAction'
import ErrorMessageBox from '../components/ErrorMessagebox'
import Loading from '../components/Loading'

export default function StartProjectScreen() {
  //const navigate = useNavigate()
  const params = useParams()
  const dispatch = useDispatch()
  const projectDetails = useSelector((state) => state.projectDetails)
  const { project, loading, error } = projectDetails

  useEffect(() => {
    dispatch(detailsOfProject(params.slug))
  })

  return (
    <>
      <Link to="/">Back to result</Link>
      {loading ? (
        <Loading />
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <Container className="mt-5">
          <Row>
            <Col md={3}>
              <Image src={project.image} alt={project.name} fluid />
            </Col>
            <Col md={6}>
              <ListGroup variant="flush">
                <ListGroupItem>
                  <h3>{project.name}</h3>
                </ListGroupItem>
                <ListGroupItem>
                  <h6>
                    <strong>Location: </strong> {project.Location}
                  </h6>
                  <h6>
                    <strong>By: </strong> {project.by}
                  </h6>
                </ListGroupItem>
                <ListGroupItem>
                  <p>
                    <strong>Description: </strong> {project.description}
                  </p>
                  <p>
                    <strong>Challenge:</strong> {project.Challenge}
                  </p>
                  <p>
                    <strong>Solution: </strong>
                    {project.Solution}
                  </p>
                  <p>
                    <strong>Longterm Impact:</strong> {project.LongTermImpact}
                  </p>
                </ListGroupItem>
              </ListGroup>
            </Col>
            <Col md={3}>
              <Card>
                <ListGroup variant="flush">
                  <ListGroupItem>
                    <h5>Category: {project.category}</h5>
                  </ListGroupItem>
                  <ListGroupItem>
                    <div>
                      Status:{' '}
                      {project.donationGoal <= project.currentdonation
                        ? 'Donation goal has been completed'
                        : 'In progress'}
                    </div>
                  </ListGroupItem>
                  <ListGroupItem>
                    <div>Donation needed: ${project.donationGoal}</div>
                    <div>Collected amount: ${project.currentdonation}</div>
                  </ListGroupItem>
                  <ListGroupItem>
                    <Button
                      className="btn-block"
                      type="button"
                      disabled={project.donationGoal <= project.currentdonation}
                    >
                      Donate
                    </Button>
                  </ListGroupItem>
                </ListGroup>
              </Card>
            </Col>
          </Row>
        </Container>
      )}
    </>
  )
}
