import {
  SAVE_PAYMENT_INFORMATION,
  ORDER_CREATE_REQUEST,
  ORDER_CREATE_SUCCESS,
  ORDER_CREATE_FAIL,
  ORDER_CREATE_RESET,
  ADD_ITEM,
} from '../constants/paymentConstant'

export const PaymentReducer = (state = { savePayment: {} }, action) => {
  switch (action.type) {
    case CART_ADD_ITEM:
      // add
      const item = action.payload
      const existItem = state.cartItems.find((x) => x.product === item.product)
      if (existItem) {
        return {
          ...state,
          cartItems: state.cartItems.map((x) =>
            x.product === existItem.product ? item : x
          ),
        }
      } else {
        return { ...state, cartItems: [...state.cartItems, item] }
      }
    case SAVE_PAYMENT_INFORMATION:
      return { ...state, savePayment: action.payload }
    default:
      return state
  }
}

export const donationCreateReducer = (state = {}, action) => {
  switch (action.type) {
    case ORDER_CREATE_REQUEST:
      return { loading: true }
    case ORDER_CREATE_SUCCESS:
      return { loading: false, order: action.payload, success: true }
    case ORDER_CREATE_FAIL:
      return { loading: false, error: action.payload }
    case ORDER_CREATE_RESET:
      return {}
    default:
      return state
  }
}
