import React, { useEffect } from 'react'
import { Row, Col } from 'react-bootstrap'
import Project from '../components/Project'
import { useDispatch, useSelector } from 'react-redux'
import { listProjects } from '../actions/projectAction'
import ErrorMessageBox from '../components/ErrorMessagebox'
import Loading from '../components/Loading'

const DiscoverProjects = () => {
  const dispatch = useDispatch()

  const projectList = useSelector((state) => state.projectList)
  const { loading, error, donation } = projectList

  useEffect(() => {
    dispatch(listProjects)
  }, [dispatch])

  return (
    <>
      <h1 className="py-2 mt-2">Charity projects</h1>
      {loading ? (
        <Loading />
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <Row className="donations">
          {donation.map((donation) => (
            <Col sm={12} md={6} lg={4} xl={3} key={donation.slug}>
              <Project donation={donation} />
            </Col>
          ))}
        </Row>
      )}
    </>
  )
}

export default DiscoverProjects
