import React, { useEffect, useState } from 'react'
import { Row, Col } from 'react-bootstrap'
import Project from '../components/Project'
import axios from 'axios'

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true }
    case 'FETCH_SUCCESS':
      return { ...state, donation: action.payload, loading: false }
    case 'FETCH_fail':
      return { ...state, loading: false, error: action.payload }
    default:
      return state
  }
}

const DiscoverProjects = () => {
  const [donation, setDonation] = useState([])
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get('/api/donation')
      setDonation(result.data)
    }
    fetchData()
  }, [])
  return (
    <>
      <h1 className="py-2 mt-2">Charity projects</h1>
      <Row className="donations">
        {donation.map((donation) => (
          <Col sm={12} md={6} lg={4} xl={3} key={donation.slug}>
            <Project donation={donation} />
          </Col>
        ))}
      </Row>
    </>
  )
}

export default DiscoverProjects
