import React, { useEffect, useReducer } from 'react'
import { Row, Col } from 'react-bootstrap'
import Project from '../components/Project'
import axios from 'axios'
import logger from 'use-reducer-logger'

const reducer = (state, action) => {
  switch (action.type) {
    case 'FETCH_REQUEST':
      return { ...state, loading: true }
    case 'FETCH_SUCCESS':
      return { ...state, donation: action.payload, loading: false }
    case 'FETCH_fail':
      return { ...state, loading: false, error: action.payload }
    default:
      return state
  }
}

const DiscoverProjects = () => {
  const [{ loading, error, projects }, dispatch] = useReducer(logger(reducer), {
    donation: [],
    loading: true,
    error: '',
  })
  //const [donation, setDonation] = useState([])
  useEffect(() => {
    const fetchData = async () => {
      dispatch({ type: 'FETCH_REQUEST' })
      try {
        const result = await axios.get('/api/project')
        dispatch({ type: 'FETCH_SUCCESS', payload: result.data })
      } catch (err) {
        dispatch({ type: 'FETCH_FAIL', payload: err.message })
      }

      //setDonation(result.data)
    }
    fetchData()
  }, [])
  return (
    <>
      <h1 className="py-2 mt-2">Charity projects</h1>
      <Row className="donations">
        {loading ? (
          <div>loading...</div>
        ) : error ? (
          <div>{error}</div>
        ) : (
          projects.map((donation) => (
            <Col sm={12} md={6} lg={4} xl={3} key={donation.slug}>
              <Project donation={donation} />
            </Col>
          ))
        )}
      </Row>
    </>
  )
}

export default DiscoverProjects
