import { SAVE_PAYMENT_INFORMATION } from '../constants/paymentConstant'

export const PaymentReducer = (
  state = { projectItem: [], savePayment: {} },
  action
) => {
  switch (action.type) {
    case SAVE_PAYMENT_INFORMATION:
      return { ...state, savePayment: action.payload }
    default:
      return state
  }
}
