import React, { useState, useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { Form, Button } from 'react-bootstrap'
import { detailsUser, updateUser } from '../actions/userAction'
import Loading from '../components/Loading'
import ErrorMessageBox from '../components/ErrorMessagebox'
import { USER_DETAILS_RESET } from '../constants/userConstant'
import { useNavigate, useParams } from 'react-router-dom'

export default function UpdateUserScreen() {
  const navigate = useNavigate()
  const params = useParams()
  const { id: userId } = params
  const [name, setName] = useState('')
  const [email, setEmail] = useState('')
  const [isNGO, setNGO] = useState('')
  const [isAdmin, setAdmin] = useState('')

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(updateUser({ _id: userId, name, email, isNGO, isAdmin }))
  }

  const userDetail = useSelector((state) => state.userDetail)
  const { user, loading, error } = userDetail
  const userUpdate = useSelector((state) => state.userUpdate)
  const {
    error: errorUpdate,
    success: successUpdate,
    loading: loadingUpdate,
  } = userUpdate
  const dispatch = useDispatch()

  useEffect(() => {
    if (successUpdate) {
      dispatch({ type: USER_DETAILS_RESET })
      navigate('/adminDashboard')
    }
    if (!user.name) {
      dispatch(detailsUser(userId))
    } else {
      setName(user.name)
      setEmail(user.email)
      setNGO(user.isNGO)
      setAdmin(user.isAdmin)
    }
  }, [user, successUpdate, dispatch, navigate, userId])
  return (
    <div>
      <h1>Edit User {userId}</h1>
      {loadingUpdate && <Loading></Loading>}
      {errorUpdate && (
        <ErrorMessageBox variant="danger">{errorUpdate}</ErrorMessageBox>
      )}
      {loading ? (
        <Loading></Loading>
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <Form onSubmit={submitHandler}>
          <Form.Group controlId="name" className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              placeholder="Enter name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group controlId="email" className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="Enter email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            ></Form.Control>
          </Form.Group>
          <Form.Group controlId="isSeller" className="mb-3">
            <Form.Check
              type="checkbox"
              label="NGO"
              checked={isNGO}
              onChange={(e) => setNGO(e.target.checked)}
            ></Form.Check>
          </Form.Group>
          <Form.Group controlId="isAdmin" className="mb-3">
            <Form.Check
              type="checkbox"
              label="Admin"
              checked={isAdmin}
              onChange={(e) => setAdmin(e.target.checked)}
            ></Form.Check>
          </Form.Group>
          <Button variant="primary" type="submit">
            Update
          </Button>
        </Form>
      )}
    </div>
  )
}
