import { createStore, combineReducers, applyMiddleware } from 'redux';
import thunk from 'redux-thunk';
import { composeWithDevTools } from 'redux-devtools-extension';
import { productListReducer, productCreateReducer, productDeleteReducer, productDetailsReducer } from './reducers/projectReducer';
import { userLoginReducer, userRegisterReducer, userUpdateReducer, userDetailsReducer, userUpdateProfileReducer } from './reducers/userReducer';
const userInfoFromStorage = localStorage.getItem('userInfo') ? JSON.parse(localStorage.getItem('userInfo')) : null;
const initialState = {
  userLogin: {
    userInfo: userInfoFromStorage
  }
};
const reducer = combineReducers({
  userLogin: userLoginReducer,
  userRegister: userRegisterReducer,
  userUpdate: userUpdateReducer,
  userDetail: userDetailsReducer,
  updateProfile: userUpdateProfileReducer,
  projectList: productListReducer,
  projectCreate: productCreateReducer,
  productDelete: productDeleteReducer,
  projectDetails: productDetailsReducer
});
const middleware = [thunk];
const store = createStore(reducer, initialState, composeWithDevTools(applyMiddleware(...middleware)));
export default store;
