import React, { useState } from 'react'
import { Form, Button, Container } from 'react-bootstrap'
import { useEffect } from 'react'
import { startAproject } from '../actions/userAction'
import { useDispatch, useSelector } from 'react-redux'
import Loading from '../components/Loading'
import ErrorMessageBox from '../components/ErrorMessagebox'
import { useNavigate, useParams } from 'react-router-dom'
import axios from 'axios'
import { START_PROJECT_RESET } from '../constants/userConstant'

export default function StartAProjectScreen() {
  const navigate = useNavigate()
  const params = useParams()
  const { id: projectId } = params
  const [uploading, setUploading] = useState(false)
  const [companyName, setCompanyName] = useState('')
  const [companylogo, setCompanylogo] = useState('')
  const [description, setDescription] = useState('')
  const [companyRegistrationNumber, setCompanyRegistrationNumber] = useState('')
  const [companyAddress, setCompanyAddress] = useState('')
  const [yearFounded, setYearFounded] = useState('')
  const [contactName, setContactName] = useState('')
  const [emailAddress, setEmailAddress] = useState('')
  const [country, setCountry] = useState('')
  const [telephoneNo, setTelephoneNo] = useState('')

  const ProjectStart = useSelector((state) => state.ProjectStart)
  const {
    error: errorUpdate,
    loading: loadingUpdate,
    success: successUpdate,
  } = ProjectStart

  const submitHandler = (e) => {
    e.preventDefault()
    dispatch(
      startAproject({
        _id: projectId,
        uploading,
        companyName,
        companylogo,
        description,
        companyRegistrationNumber,
        companyAddress,
        yearFounded,
        contactName,
        emailAddress,
        country,
        telephoneNo,
      })
    )
  }

  const dispatch = useDispatch()
  useEffect(() => {
    if (successUpdate) {
      dispatch({ type: START_PROJECT_RESET })
      navigate('/adminDashboard')
    }
  }, [successUpdate, navigate, dispatch])

  const uploadFileHandler = async (e) => {
    const file = e.target.files[0]
    const bodyformData = new FormData()
    bodyformData.append('file', file)
    setUploading(true)
    try {
      const { data } = await axios.post('/api/upload', bodyformData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      })
      setCompanylogo(data.secure_url)
      setUploading(false)
    } catch (error) {
      console.error(error)
      setUploading(false)
    }
  }

  return (
    <Container>
      <h1>Start a project</h1>
      {loadingUpdate && <Loading></Loading>}
      {errorUpdate && (
        <ErrorMessageBox variant="danger">{errorUpdate}</ErrorMessageBox>
      )}
      {successUpdate && (
        <ErrorMessageBox variant="danger">{successUpdate}</ErrorMessageBox>
      )}
      <Form onSubmit={submitHandler}>
        <Form.Group controlId="Company Name" className="mb-3">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Company Name"
            value={companyName}
            onChange={(e) => setCompanyName(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Form.Group controlId="Company Description" className="mb-3">
          <Form.Label>Category</Form.Label>
          <Form.Control
            as="textarea"
            placeholder="Enter company description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Form.Group controlId="image" className="mb-3">
          <Form.Label>Image</Form.Label>
          <Form.Control
            type="text"
            placeholder="select image"
            value={companylogo}
            onChange={(e) => setCompanylogo(e.target.value)}
          ></Form.Control>
          <Form.Group controlId="imageFile">
            <Form.Label>Upload image</Form.Label>
            <Form.Control
              type="file"
              onChange={uploadFileHandler}
            ></Form.Control>
            {uploading && <Loading />}
          </Form.Group>
        </Form.Group>
        <Form.Group controlId="Company Registration Number" className="mb-3">
          <Form.Label>Company Registration Number</Form.Label>
          <Form.Control
            type="number"
            placeholder="Enter your Company Registration Number etc: 2019 xx xxxx"
            value={companyRegistrationNumber}
            required
            onChange={(e) => setCompanyRegistrationNumber(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="Company Address" className="mb-3">
          <Form.Label>Company Address</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Company Address"
            value={companyAddress}
            onChange={(e) => setCompanyAddress(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Form.Group controlId="yearFounded" className="mb-3">
          <Form.Label>Company year founded</Form.Label>
          <Form.Control
            type="number"
            placeholder="Enter your Company year founded"
            value={yearFounded}
            required
            onChange={(e) => setYearFounded(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="contactName" className="mb-3">
          <Form.Label>Company contact Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Company contact Name"
            value={contactName}
            onChange={(e) => setContactName(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Form.Group controlId="telephoneNo" className="mb-3">
          <Form.Label>company contact Number</Form.Label>
          <Form.Control
            type="number"
            placeholder="Enter your company contact Number"
            value={telephoneNo}
            required
            onChange={(e) => setTelephoneNo(e.target.value)}
          />
        </Form.Group>
        <Form.Group controlId="emailAddress" className="mb-3">
          <Form.Label>Company Email Address</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Company Email Address"
            value={emailAddress}
            onChange={(e) => setEmailAddress(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Form.Group controlId="country" className="mb-3">
          <Form.Label>Company country from</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Country Name"
            value={country}
            onChange={(e) => setCountry(e.target.value)}
          ></Form.Control>
        </Form.Group>
        <Button variant="primary" type="submit">
          Update
        </Button>{' '}
      </Form>
    </Container>
  )
}
