import React, { useEffect } from 'react'
import { Button, Table, Row, Col, Pagination } from 'react-bootstrap'
import { LinkContainer } from 'react-router-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import {
  listProjects,
  deleteProject,
  createProject,
} from '../actions/projectAction'
import Loading from '../components/Loading'
import ErrorMessageBox from '../components/ErrorMessagebox'
import { PROJECT_CREATE_RESET } from '../constants/projectConstant'
import { useLocation, useNavigate, useParams } from 'react-router-dom'

export default function CreateProjectScreen(props) {
  const navigate = useNavigate()
  const { pathname } = useLocation()
  const params = useParams()
  const NGOMode = pathname.indexOf('/NGO') >= 0
  const dispatch = useDispatch()

  const pageNumber = params.pageNumber || 1
  const createHandler = () => {
    dispatch(createProject())
  }
  const deleteHandler = (project) => {
    if (window.confirm('Are you sure?')) {
      dispatch(deleteProject(project._id))
    }
  }

  const productDelete = useSelector((state) => state.productDelete)
  const {
    success: successDelete,
    loading: loadingDelete,
    error: errorDelete,
  } = productDelete
  const userLogin = useSelector((state) => state.userLogin)
  const { userInfo } = userLogin
  const projectList = useSelector((state) => state.productList)
  const { loading, error, Projects, pages, page } = projectList

  const projectCreate = useSelector((state) => state.projectCreate)
  const {
    success: successCreate,
    loading: loadingCreate,
    error: errorCreate,
    project: createdProduct,
  } = projectCreate

  useEffect(() => {
    if (successCreate) {
      dispatch({ type: PROJECT_CREATE_RESET })
      navigate(`/product/${createdProduct._id}/edit`)
    } else {
      dispatch(listProjects({ NGO: NGOMode ? userInfo._id : '', pageNumber }))
    }
  }, [successDelete, successCreate, pageNumber, dispatch])
  return (
    <>
      <Row className="align-items-center">
        <Col>
          <h1>Projects</h1>
        </Col>
        <Col className="text-right">
          <Button onClick={createHandler}>Create Product</Button>
        </Col>
      </Row>
      {loadingCreate && <Loading></Loading>}
      {errorCreate && (
        <ErrorMessageBox variant="danger">{errorCreate}</ErrorMessageBox>
      )}
      {loadingDelete && <Loading></Loading>}
      {errorDelete && (
        <ErrorMessageBox variant="danger">{errorDelete}</ErrorMessageBox>
      )}
      {loading ? (
        <Loading></Loading>
      ) : error ? (
        <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
      ) : (
        <>
          <Table striped bordered hover responsive>
            <thead>
              <tr>
                <th>ID</th>
                <th>NAME</th>
                <th>SELLER</th>
                <th>PRICE</th>
                <th>CATEGORY</th>
                <th>BRAND</th>
                <th>ACTIONS</th>
              </tr>
            </thead>
            <tbody>
              {Projects.map((project) => (
                <tr key={project._id}>
                  <td>{project._id}</td>
                  <td>{project.name}</td>
                  <td>{project.NGO.name}</td>
                  <td>{project.donationGoal}</td>
                  <td>{project.description}</td>
                  <td>{project.country}</td>
                  <td>
                    <LinkContainer to={`/Projects/${project._id}/edit`}>
                      <Button variant="light" className="btn-sm">
                        Edit
                      </Button>
                    </LinkContainer>
                    <Button
                      type="button"
                      variant="light"
                      className="btn-sm"
                      onClick={() => deleteHandler(project)}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
          <Pagination>
            {[...Array(pages).keys()].map((x) => (
              <LinkContainer key={x + 1} to={`/projectlist/${x + 1}`}>
                <Pagination.Item active={x + 1 === page}>
                  {x + 1}
                </Pagination.Item>
              </LinkContainer>
            ))}
          </Pagination>
        </>
      )}
    </>
  )
}
