import { createStore, combineReducers, applyMiddleware } from 'redux'
import thunk from 'redux-thunk'
import { composeWithDevTools } from 'redux-devtools-extension'
import {
  projectListReducer,
  projectCreateReducer,
  projectDeleteReducer,
  projectDetailsReducer,
  projectUpdateReducer,
} from './reducers/projectReducer'

import {
  userLoginReducer,
  userRegisterReducer,
  userUpdateReducer,
  userDetailsReducer,
  userUpdateProfileReducer,
  userListReducer,
  userDeleteReducer,
  projectStartReducer,
  userNGOreqReducer,
} from './reducers/userReducer'
import { PaymentReducer } from './reducers/paymentReducer'

// const userInfoFromStorage = localStorage.getItem('userInfo')
//   ? JSON.parse(localStorage.getItem('userInfo'))
//   : null
const userInfo = localStorage.getItem('userInfo')
  ? JSON.parse(localStorage.getItem('userInfo'))
  : null

const initialState = {
  userLogin: { userInfo },
  payment: {
    // paymmentItem: localStorage.getItem('paymentItem')
    //   ? JSON.parse(localStorage.getItem('paymentItem'))
    //   : [],
    savePayment: localStorage.getItem('savePayment')
      ? JSON.parse(localStorage.getItem('paymentItem'))
      : [],
  },
}
const reducer = combineReducers({
  userLogin: userLoginReducer,
  userRegister: userRegisterReducer,
  userUpdate: userUpdateReducer,
  userDetail: userDetailsReducer,
  userList: userListReducer,
  userDelete: userDeleteReducer,
  updateProfile: userUpdateProfileReducer,
  listNGO: userNGOreqReducer,
  projectList: projectListReducer,
  projectCreate: projectCreateReducer,
  projectDelete: projectDeleteReducer,
  projectDetails: projectDetailsReducer,
  projectUpdate: projectUpdateReducer,
  ProjectStart: projectStartReducer,
  payment: PaymentReducer,
})

const middleware = [thunk]

const store = createStore(
  reducer,
  initialState,
  composeWithDevTools(applyMiddleware(...middleware))
)
export default store
