import React, { useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import { useSelector, useDispatch } from 'react-redux'
import {
  Row,
  Col,
  ListGroup,
  ListGroupItem,
  Card,
  Button,
} from 'react-bootstrap'
import CheckoutSteps from '../components/paymentStep'
// import ErrorMessageBox from '../components/ErrorMessagebox'
// import { createOrder } from '../actions/orderActions'

export default function PaymentScreen() {
  const navigate = useNavigate()
  const payment = useSelector((state) => state.payment)

  payment.taxPrice = Number((0.11 * payment.savePayment.donation).toFixed(2))
  payment.totalPrice = payment.taxPrice + payment.savePayment.donation
  const dispatch = useDispatch()
  const placeOrderHandler = () => {
    // dispatch(
    //   createOrder({
    //     orderItems: payment.cartItems,
    //     shippingAddress: payment.shippingAddress,
    //     paymentMethod: payment.paymentMethod,
    //     itemsPrice: payment.itemsPrice,
    //     shippingPrice: payment.shippingPrice,
    //     taxPrice: payment.taxPrice,
    //     totalPrice: payment.totalPrice,
    //   })
    // )
  }
  //   const orderCreate = useSelector((state) => state.orderCreate)
  //   const { success, order, error } = orderCreate
  //   useEffect(() => {
  //     if (success) {
  //       navigate(`/order/${order._id}`)
  //     }
  //   }, [success, navigate])
  return (
    <>
      <CheckoutSteps step1 step2></CheckoutSteps>
      <Row>
        <Col md={8}>
          <ListGroup variant="flush">
            <ListGroupItem>
              <h2>Detail</h2>
              <p>Name: {payment.savePayment.name}</p>
              <p>Email: {payment.savePayment.email}</p>
              <p>Description: {payment.savePayment.description}</p>
            </ListGroupItem>
            <ListGroupItem>
              <h2>Payment</h2>
              <strong>Method: </strong>
              {payment.paymentMethod}
            </ListGroupItem>
          </ListGroup>
        </Col>
        <Col md={4}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <h2>Donation Summary</h2>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Donation amount</Col>
                  <Col>${payment.savePayment.donation}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Tax</Col>
                  <Col>${payment.taxPrice}</Col>
                </Row>
              </ListGroup.Item>
              <ListGroup.Item>
                <Row>
                  <Col>Total</Col>
                  <Col>${payment.totalPrice}</Col>
                </Row>
              </ListGroup.Item>
              {/* {error && (
                <ListGroup.Item>
                  <ErrorMessageBox variant="danger">{error}</ErrorMessageBox>
                </ListGroup.Item>
              )} */}

              <ListGroup.Item>
                <Button
                  type="button"
                  className="btn-block"
                  onClick={placeOrderHandler}
                >
                  Donate
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </>
  )
}
